mapper.select:
SELECT u.u_id, u.u_name, u.u_account_name, u.u_password, u.u_delete_status, u.u_locked, u.u_description, u.u_credentials_salt, u.u_creator_name, u.u_create_time, u.u_update_time, r.r_id, r.r_name, r.r_key, r.r_status, r.r_description, r.r_create_time, r.r_update_time FROM tb_user u LEFT JOIN tb_role_user ru ON u.u_id = ru.u_id LEFT JOIN tb_role r ON ru.r_id = r.r_id
and u.u_creator_name = #{creatorName}
order by u_id DESC

mapper.update:
update tb_role_user
set r_id = ${role.id}
where u_id = ${id}
